--

CREATE FUNCTION public.agenda_ultima_data(agenda public.agendas) RETURNS date
    LANGUAGE plpgsql IMMUTABLE
    AS $$
      DECLARE
        data DATE;
      BEGIN
        SELECT (CURRENT_DATE + (COALESCE(agenda.dias_agendamento,
          unidades_saude.dias_agendamento,
          ferramentas_parametrizacoes.dias_agendamento) || ' days')::interval)
          INTO data
        FROM ferramentas_parametrizacoes
        LEFT JOIN unidades_saude_ocupacoes
          ON unidades_saude_ocupacoes.id = agenda.unidade_saude_ocupacao_id
        LEFT JOIN (SELECT id, dias_agendamento FROM unidades_saude) unidades_saude
          ON unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id
        LIMIT 1;
        RETURN data;
      END;
      $$;


