--

CREATE FUNCTION public.agendamento_tipo_atividade(agendamento public.agendamentos) RETURNS integer
    LANGUAGE plpgsql IMMUTABLE
    AS $$
      DECLARE
        ordem_atividade INTEGER;
      BEGIN
        CASE
          WHEN agendamento.regulacao_retorno IS TRUE THEN
            ordem_atividade := 5;
          WHEN agendamento.regulacao IS TRUE THEN
            ordem_atividade := 4;
          WHEN agendamento.nova_consulta IS TRUE THEN
            ordem_atividade := 1;
          WHEN agendamento.reserva IS TRUE THEN
            ordem_atividade := 3;
          ELSE
            ordem_atividade := 2;
        END CASE;

        RETURN (SELECT id FROM tipos_atividades_agendamentos
          WHERE tipos_atividades_agendamentos.ordem = ordem_atividade LIMIT 1);
      END;
      $$;


--
-- Name: agendamentos_retroativos_datas_bloqueadas(bigint); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.agendamentos_retroativos_datas_bloqueadas(agenda_busca_id bigint) RETURNS date[]
    LANGUAGE plpgsql IMMUTABLE
    AS $$
      DECLARE
        datas date[];
        periodo INTEGER;
        data_inicial timestamp;
        data_final timestamp;
      BEGIN
        SELECT periodo_agendamento_retroativo INTO periodo FROM ferramentas_parametrizacoes LIMIT 1;

        data_inicial := (current_date - periodo)::timestamp;
        data_final := (current_date - 1)::timestamp;

        WITH analise_vagas AS (
          SELECT data FROM vw_analise_vagas_agendas
          WHERE agenda_id = agenda_busca_id AND data BETWEEN data_inicial AND data_final
        )
        SELECT array_agg(data) into datas FROM (
          SELECT generate_series(data_inicial, data_final, '1 day'::interval)::date as data
        ) datas_periodo
        WHERE NOT EXISTS (
          SELECT 1 FROM analise_vagas WHERE analise_vagas.data = datas_periodo.data
        );

        return datas;
      END;
      $$;


