-- Name: calcula_idade_gestacional(timestamp without time zone); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.calcula_idade_gestacional(dum timestamp without time zone) RETURNS text
    LANGUAGE plpgsql IMMUTABLE
    AS $$
      BEGIN
        return
          case
            when (((dum + interval '7 days') + interval '9 months') > CURRENT_DATE)
            then (div(to_char(now() - dum, 'DD')::integer, 7)) || ' sem. e ' ||
               (mod(to_char(now() - dum, 'DD')::integer, 7)) || ' dias'
            else ''
          end;
        END;
      $$;


--
-- Name: calculate_age(date); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.calculate_age(birthdate date) RETURNS text
    LANGUAGE plpgsql
    AS $$
      DECLARE
          age_interval interval;
          years integer;
          months integer;
          days integer;
      BEGIN
          SELECT age(birthdate) INTO age_interval;
          years := extract(year from age_interval);
          months := extract(month from age_interval);
          days := extract(day from age_interval);
      
          RETURN 
            CASE
              WHEN years >= 10 THEN to_char(years, 'FM000') || 'a '
              ELSE to_char(years, 'FM09') || 'a '
            END || 
            to_char(months, 'FM09') || 'm ' || 
            to_char(days, 'FM09') || 'd';
      END;
      $$;


