CREATE FUNCTION public.endereco_completo(municipe bigint) RETURNS character varying
    LANGUAGE plpgsql IMMUTABLE
    AS $$
        DECLARE
          endereco_completo varchar;
        BEGIN
          select
            concat(
              coalesce(concat(tipos_logradouro.nome || ' '), '') ||
              coalesce(concat(municipes.residencia_logradouro || ', '), '') ||
              coalesce(concat(municipes.residencia_numero || ', '), '') ||
              coalesce(concat(municipes.residencia_bairro || ' '), '') ||
              coalesce(concat('- CEP: ' || municipes.residencia_cep), '') ||
              coalesce(concat(' - ' || municipios.nome), '')
            ) into endereco_completo
          from municipes
          inner join tipos_logradouro on (tipos_logradouro.id = municipes.tipo_logradouro_id)
          inner join municipios on (municipios.id = municipes.residencia_municipio_id)
          where municipes.id = municipe;

          RETURN endereco_completo;
        END;
      $$;


