-- Name: func_boolean_dynamic_table_cadastros_domiciliares(character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.func_boolean_dynamic_table_cadastros_domiciliares(col character varying) RETURNS TABLE(tabela character varying, nome character varying, valor bigint, qtde bigint, unidade_saude character varying, unidade_saude_id bigint, area integer, micro_area integer, fora_area boolean, identificador character varying)
    LANGUAGE plpgsql
    AS $$
      BEGIN
      RETURN QUERY

      execute 'select ' || '''' || col || '''' ||'::varchar as tabela,
      case
      when cadastros_domiciliares.' || col || ' then ' || '''' || 'Sim' || '''' || '
      when not cadastros_domiciliares.' || col || ' then ' || '''' || 'Não' || '''' || '
      end::varchar as nome,
      cadastros_domiciliares.' || col || '::integer::bigint as valor,
      count(*)::bigint as qtde,
      unidades_saude.nome as unidade_saude,
      unidades_saude.id::bigint as unidade_saude_id,
      equipes.area as area,
      equipes_profissionais.codigo_micro_area as micro_area,
      cadastros_domiciliares.st_fora_area as fora_area,
      ( ' || '''' || col || '-' || '''' || ' || ' || col || '::INTEGER)::VARCHAR as identificador
      from cadastros_domiciliares
      inner join unidades_saude on (unidades_saude.id = cadastros_domiciliares.unidade_saude_id)
      inner join equipes on equipes.unidade_saude_id = unidades_saude.id
      left join equipes_profissionais on equipes_profissionais.equipe_id = equipes.id
      and equipes_profissionais.profissional_id = cadastros_domiciliares.profissional_id
      where cadastros_domiciliares.' || col || ' is not null
      group by cadastros_domiciliares.' || col || ',
      unidades_saude.nome,
      unidades_saude.id,
      equipes.area,
      equipes_profissionais.codigo_micro_area,
      cadastros_domiciliares.st_fora_area';
      END;
      $$;


