CREATE FUNCTION public.func_boolean_dynamic_table_visitas_domiciliares(col character varying) RETURNS TABLE(tabela character varying, nome character varying, valor bigint, qtde bigint, unidade_saude character varying, unidade_saude_id bigint, area integer, micro_area integer, fora_area boolean, identificador character varying)
    LANGUAGE plpgsql
    AS $$
      BEGIN
      RETURN QUERY

      execute 'select ' || '''' || col || '''' ||'::varchar as tabela,
      case
      when esus_fichas_visitas_domiciliares.' || col || ' then ' || '''' || 'Sim' || '''' || '
      when not esus_fichas_visitas_domiciliares.' || col || ' then ' || '''' || 'Não' || '''' || '
      end::varchar as nome,
      esus_fichas_visitas_domiciliares.' || col || '::integer::bigint as valor,
      count(*)::bigint as qtde,
      unidades_saude.nome as unidade_saude,
      unidades_saude.id::bigint as unidade_saude_id,
      equipes.area as area,
      nullif(esus_fichas_visitas_domiciliares.st_micro_area,'''')::integer as micro_area,
      esus_fichas_visitas_domiciliares.st_fora_area as fora_area,
      ( ' || '''' || col || '-' || '''' || ' || cast(' || col || ' as TEXT))::VARCHAR as identificador
      from esus_fichas_visitas_domiciliares
      inner join municipes on (municipes.id = esus_fichas_visitas_domiciliares.municipe_id)
      inner join unidades_saude on (unidades_saude.id = esus_fichas_visitas_domiciliares.unidade_saude_id)
      inner join equipes on (equipes.id = municipes.equipe_id)
      where esus_fichas_visitas_domiciliares.' || col || ' is not null
      group by esus_fichas_visitas_domiciliares.' || col || ',
      unidades_saude.nome,
      unidades_saude.id,
      equipes.area,
      esus_fichas_visitas_domiciliares.st_micro_area,
      esus_fichas_visitas_domiciliares.st_fora_area';
      END;
      $$;


