-- Name: func_formata_arquivo_bpa(character varying, date, date, bigint, character varying, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.func_formata_arquivo_bpa(unidade_saude_id character varying, data_inicio date, data_fim date, arquivo_controle bigint, referencia character varying, tipo_fat character varying) RETURNS void
    LANGUAGE plpgsql
    AS $_$
        DECLARE
          busca text := (SELECT query
                           FROM faturamento_lancamentos_bpa_queries
                          WHERE faturamento_lancamentos_bpa_queries.model = referencia::varchar
                            AND faturamento_lancamentos_bpa_queries.tipo = tipo_fat::varchar);
          valores record;
        BEGIN
          FOR valores IN EXECUTE
              FORMAT(busca,
                     unidade_saude_id,
                     data_inicio,
                     data_fim,
                     tipo_fat,
                     arquivo_controle)
          LOOP
            IF tipo_fat = 'C' THEN
              EXECUTE FORMAT('INSERT INTO faturamento_tmp_lancamentos_bpa_c_linhas(quantidade,
                                                                                   codigo_procedimento,
                                                                                   idade,
                                                                                   codigo_cnes,
                                                                                   codigo_ocupacao,
                                                                                   controle_arquivo_bpa_id)
                              VALUES %1$s ', TRANSLATE(valores::varchar,'"',''));
            ELSE
              EXECUTE FORMAT('INSERT INTO faturamento_tmp_lancamentos_bpa_i_linhas(faturavel_id,
                                                                                   faturavel_type,
                                                                                   controle_arquivo_bpa_id,
                                                                                   codigo_cnes,
                                                                                   data,
                                                                                   codigo_cns_profissional,
                                                                                   codigo_ocupacao,
                                                                                   data_consulta,
                                                                                   codigo_procedimento,
                                                                                   codigo_cns,
                                                                                   sexo,
                                                                                   codigo_ibge,
                                                                                   cid,
                                                                                   idade,
                                                                                   quantidade,
                                                                                   carater_atendimento,
                                                                                   numero_autorizacao,
                                                                                   nome,
                                                                                   data_nascimento,
                                                                                   raca,
                                                                                   etnia,
                                                                                   nacionalidade,
                                                                                   codigo_servico,
                                                                                   codigo_classificacao,
                                                                                   codigo_sequencia_equipe,
                                                                                   codigo_area_equipe,
                                                                                   cnpj_opm,
                                                                                   cep_paciente,
                                                                                   cod_logradouro,
                                                                                   endereco_paciente,
                                                                                   complemento_endereco,
                                                                                   numero_endereco,
                                                                                   bairro_endereco,
                                                                                   telefone_paciente,
                                                                                   email_paciente)
                              VALUES %1$s ', TRANSLATE(valores::varchar,'"',''));
            END IF;
          END LOOP;
        END
      $_$;


--
-- Name: func_formata_campo_arquivo_bpa(character varying, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.func_formata_campo_arquivo_bpa(valor character varying, coluna character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
      DECLARE
        resultado VARCHAR;
        tam_campo INTEGER;
        char_preench VARCHAR := '0';
        al_direita BOOLEAN := true;
        valor_form VARCHAR := coalesce(valor, '0');
        sexo VARCHAR;
        etnia_indigena BOOLEAN := false;
      BEGIN
        --CAMPOS BPA C
        IF coluna = 'quantidade' THEN
          tam_campo := 6;
        ELSIF coluna = 'codigo_procedimento' THEN
          tam_campo := 10;
        ELSIF coluna = 'idade' THEN
          IF valor_form = '999' THEN
            valor_form := '   ';
          END IF;
          tam_campo := 3;
        ELSIF coluna = 'codigo_cnes' THEN
          tam_campo := 7;
        ELSIF coluna = 'codigo_ocupacao' THEN
          tam_campo := 6;

        --CAMPOS BPA I
        ELSIF coluna = 'codigo_cnes' THEN
          tam_campo := 7;
        ELSIF coluna = 'data' THEN
          tam_campo := 6;
          char_preench := chr(32);
        ELSIF coluna = 'codigo_cns_profissional' THEN
          tam_campo := 15;
        ELSIF coluna = 'codigo_ocupacao' THEN
          tam_campo := 6;
        ELSIF coluna = 'data_consulta' THEN
          valor_form := to_char(valor_form::date, 'YYYYMMDD');
          tam_campo := 8;
        ELSIF coluna = 'procedimento' THEN
          tam_campo := 10;
        ELSIF coluna = 'codigo_cns' THEN
          tam_campo := 15;
        ELSIF coluna = 'sexo' THEN
          IF valor_form = 'M' THEN
            valor_form := 'M';
          ELSE
            valor_form := 'F';
          END IF;
          tam_campo := 1;
        ELSIF coluna = 'codigo_ibge' THEN
          tam_campo := 6;
        ELSIF coluna = 'cid' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 4;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'idade' THEN
          tam_campo := 3;
        ELSIF coluna = 'quantidade' THEN
          tam_campo := 6;
        ELSIF coluna = 'carater_atendimento' THEN
          tam_campo := 2;
        ELSIF coluna = 'numero_autorizacao' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 13;
          char_preench := chr(32);
        ELSIF coluna = 'nome' THEN
          tam_campo := 30;
          al_direita := false;
          char_preench := chr(32);
        ELSIF coluna = 'data_nascimento' THEN
          valor_form := to_char(valor_form::date, 'YYYYMMDD');
          tam_campo := 8;
        ELSIF coluna = 'raca' THEN
          IF valor_form = '05' THEN
            etnia_indigena := true;
          END IF;
          IF valor_form = '0' THEN
            valor_form := '99';
          END IF;
          tam_campo := 2;
        ELSIF coluna = 'etnia' THEN
          IF valor_form = '0' OR etnia_indigena = false THEN
            valor_form := chr(32);
            char_preench := chr(32);
          END IF;
          tam_campo := 4;
        ELSIF coluna = 'nacionalidade' THEN
          IF valor_form = '0' THEN
            valor_form := '010';
          END IF;
          tam_campo := 3;
        ELSIF coluna = 'codigo_servico' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 3;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'codigo_classificacao' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 3;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'codigo_sequencia_equipe' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 8;
          char_preench := chr(32);
        ELSIF coluna = 'codigo_area_equipe' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 4;
          char_preench := chr(32);
        ELSIF coluna = 'cnpj_opm' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 14;
          char_preench := chr(32);
        ELSIF coluna = 'cep_paciente' THEN
          tam_campo := 8;
          valor_form := regexp_replace(valor_form,'[^0-9]','','g');
          IF valor_form = '00000000' THEN
            valor_form := '06717900';
          END IF;
        ELSIF coluna = 'cod_logradouro' THEN
          IF valor_form = '0' THEN
            valor_form := '081';
          END IF;
          tam_campo := 3;
          char_preench := chr(32);
        ELSIF coluna = 'endereco_paciente' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 30;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'complemento_endereco' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 10;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'numero_endereco' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 5;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'bairro_endereco' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 30;
          char_preench := chr(32);
          al_direita := false;
        ELSIF coluna = 'telefone_paciente' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 11;
          al_direita := false;
          valor_form := regexp_replace(valor_form,'[^0-9]','','g');
          char_preench := chr(32);
        ELSIF coluna = 'email_paciente' THEN
          IF valor_form = '0' THEN
            valor_form := chr(32);
          END IF;
          tam_campo := 40;
          char_preench := chr(32);
          al_direita := false;
        END IF;

        IF al_direita THEN
          resultado := chr(36) || chr(36) || lpad(left(CAST(valor_form AS VARCHAR),tam_campo),tam_campo, char_preench) || chr(36) || chr(36);
        ELSE
          resultado := chr(36) || chr(36) || rpad(left(CAST(valor_form AS VARCHAR),tam_campo),tam_campo, char_preench) || chr(36) || chr(36);
        END IF;

        RETURN resultado;
      END;
      $$;


