--
-- Name: pega_situacao_presenca_agenda(bigint); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.pega_situacao_presenca_agenda(bigint) RETURNS integer
    LANGUAGE sql IMMUTABLE STRICT
    AS $_$select    CASE
            WHEN ambulatorial_situacoes_presencas_atendimentos.id IS NOT NULL
            THEN ambulatorial_situacoes_presencas_atendimentos.id
            WHEN agendamentos.retroativo = true
            THEN 1
            WHEN agendamentos.canceled_at IS NOT NULL
            THEN 5
            WHEN now()::text::date > agendamentos.data
            OR agendamentos.data = now()::text::date AND now()::text::time with time zone >= (agendamentos.hora + 3600::text::interval)::time with time zone
            THEN 2
            WHEN agendamentos.confirmado = true
            THEN 4
            ELSE 6
        END AS ambulatorial_situacao_presenca_atendimento_id
        FROM agendamentos
        LEFT OUTER JOIN "atendimentos_ambulatoriais"
        ON "atendimentos_ambulatoriais"."agendamento_id" = "agendamentos"."id"
        LEFT OUTER JOIN "situacoes_presencas_atendimentos"  as ambulatorial_situacoes_presencas_atendimentos
        ON "ambulatorial_situacoes_presencas_atendimentos"."id" = "atendimentos_ambulatoriais"."situacao_presenca_atendimento_id"
        WHERE agendamentos.id = $1$_$;


