--
-- Name: valida_cns(character varying, character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.valida_cns(numero_cns character varying, numero_provisorio character varying) RETURNS boolean
    LANGUAGE plpgsql IMMUTABLE
    AS $$
        DECLARE
          soma float;
          resto float;
          dv float;
          pis varchar;
          resultado varchar;
          primeiro_digito char;
          cns varchar;
        BEGIN
          IF length(numero_cns) >= 15 THEN
            cns := numero_cns;
          ELSIF length(numero_provisorio) >= 15 THEN
            cns := numero_provisorio;
          ELSE
            RETURN FALSE;
          END IF;

          cns := substring(trim(cns), 1, 15);

          IF length(cns) != 15 THEN
            RETURN FALSE;
          END IF;

          primeiro_digito := substr(cns, 1, 1);

          IF primeiro_digito = '1' OR primeiro_digito = '2' THEN
            pis := substring(cns, 1, 11);

            soma := (substring(pis, 1, 1)::integer * 15) +
            (substring(pis, 2, 1)::integer * 14) +
            (substring(pis, 3, 1)::integer * 13) +
            (substring(pis, 4, 1)::integer * 12) +
            (substring(pis, 5, 1)::integer * 11) +
            (substring(pis, 6, 1)::integer * 10) +
            (substring(pis, 7, 1)::integer * 9) +
            (substring(pis, 8, 1)::integer * 8) +
            (substring(pis, 9, 1)::integer * 7) +
            (substring(pis, 10, 1)::integer * 6) +
            (substring(pis, 11, 1)::integer * 5);

            resto := soma::integer % 11::integer;
            dv := 11 - resto;

            IF dv = 11 THEN
              dv := 0;
            END IF;

            IF dv = 10 THEN
              soma := (substring(pis, 1, 1)::integer * 15) +
                (substring(pis, 2, 1)::integer * 14) +
                (substring(pis, 3, 1)::integer * 13) +
                (substring(pis, 4, 1)::integer * 12) +
                (substring(pis, 5, 1)::integer * 11) +
                (substring(pis, 6, 1)::integer * 10) +
                (substring(pis, 7, 1)::integer * 9) +
                (substring(pis, 8, 1)::integer * 8) +
                (substring(pis, 9, 1)::integer * 7) +
                (substring(pis, 10, 1)::integer * 6) +
                (substring(pis, 11, 1)::integer * 5) + 2;

              resto := soma::integer % 11::integer;
              dv := 11 - resto;
              resultado := pis || '001' || dv::varchar;
            ELSE
              resultado := pis || '000' || dv::varchar;
            END IF;

            IF cns != resultado THEN
              RETURN FALSE;
            ELSE
              RETURN TRUE;
            END IF;
          ELSIF primeiro_digito = '7' OR primeiro_digito = '8' OR  primeiro_digito = '9' THEN
              pis := substring(cns, 1, 15);

              soma := (substring(pis, 1, 1)::integer * 15) +
                (substring(pis, 2, 1)::integer * 14) +
                (substring(pis, 3, 1)::integer * 13) +
                (substring(pis, 4, 1)::integer * 12) +
                (substring(pis, 5, 1)::integer * 11) +
                (substring(pis, 6, 1)::integer * 10) +
                (substring(pis, 7, 1)::integer * 9) +
                (substring(pis, 8, 1)::integer * 8) +
                (substring(pis, 9, 1)::integer * 7) +
                (substring(pis, 10, 1)::integer * 6) +
                (substring(pis, 11, 1)::integer * 5) +
                (substring(pis, 12, 1)::integer * 4) +
                (substring(pis, 13, 1)::integer * 3) +
                (substring(pis, 14, 1)::integer * 2) +
                (substring(pis, 15, 1)::integer * 1);

                resto := soma::integer % 11::integer;

                IF resto != 0 THEN
                  RETURN FALSE;
                ELSE
                  RETURN TRUE;
                END IF;
          ELSE
            RETURN FALSE;
          END IF;
  	    EXCEPTION
  	      WHEN OTHERS THEN
  	      RETURN FALSE;
        END;
      $$;


