CREATE FUNCTION public.valida_procedimento_idade(lancamento public.faturamento_lancamentos_bpa_c) RETURNS boolean
    LANGUAGE plpgsql IMMUTABLE
    AS $$
            DECLARE
              procedimento datasus_procedimentos;
              municipe_idade integer;
            BEGIN
              SELECT datasus_procedimentos.* INTO procedimento FROM datasus_procedimentos
            	INNER JOIN procedimentos ON procedimentos.codigo = datasus_procedimentos.co_procedimento
            	INNER JOIN faturamento_lancamentos_bpa_c ON faturamento_lancamentos_bpa_c.procedimento_id = procedimentos.id
            	WHERE faturamento_lancamentos_bpa_c.id = lancamento.id
            	AND datasus_procedimentos.dt_competencia = datasus_competencia_atual()
            	LIMIT 1;

              SELECT idade_em_meses(municipes.data_nascimento) INTO municipe_idade FROM municipes INNER JOIN faturamento_lancamentos_bpa_c ON faturamento_lancamentos_bpa_c.municipe_id = municipes.id WHERE faturamento_lancamentos_bpa_c.id = lancamento.id;

              IF procedimento.vl_idade_minima = 9999 AND procedimento.vl_idade_maxima = 9999 THEN
                RETURN TRUE;
              ELSE
                RETURN municipe_idade BETWEEN procedimento.vl_idade_minima AND procedimento.vl_idade_maxima;
              END IF;
            END;
            $$;


