--
-- Name: pedidos_requisicao; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.pedidos_requisicao AS
 SELECT estoque_produtos.codigo AS produto_codigo,
    estoque_produtos.nome AS produto_nome,
    estoque_lotes.produto_id,
    estoque_unidades_medidas.nome AS unidade_medida_nome,
    estoque_movimentacoes.almoxarifado_id,
    estoque_produtos.produto_grupo_id,
    estoque_movimentacoes.created_at,
    COALESCE(saldo_atual_query.saldo_atual, (0)::numeric) AS saldo_atual,
    COALESCE(saldo_municipe_query.saldo_municipe, (0)::numeric) AS saidas_municipes,
    COALESCE(saldo_requisicao_consumo_query.saldo_requisicao_consumo, (0)::numeric) AS requisicao_consumo,
    (COALESCE(saldo_municipe_query.saldo_municipe, (0)::numeric) + COALESCE(saldo_requisicao_consumo_query.saldo_requisicao_consumo, (0)::numeric)) AS total_saidas,
        CASE
            WHEN ((COALESCE(saldo_municipe_query.saldo_municipe, (0)::numeric) + COALESCE(saldo_requisicao_consumo_query.saldo_requisicao_consumo, (0)::numeric)) > COALESCE(saldo_atual_query.saldo_atual, (0)::numeric)) THEN (((COALESCE(saldo_municipe_query.saldo_municipe, (0)::numeric) + COALESCE(saldo_requisicao_consumo_query.saldo_requisicao_consumo, (0)::numeric)) - COALESCE(saldo_atual_query.saldo_atual, (0)::numeric)) * 1.25)
            ELSE (0)::numeric
        END AS necessidade,
    COALESCE(ultimos_pedidos_reposicoes.triagem, (0)::numeric) AS ultima_triagem
   FROM (((((((public.estoque_produtos
     JOIN public.estoque_lotes ON ((estoque_lotes.produto_id = estoque_produtos.id)))
     JOIN public.estoque_movimentacoes ON ((estoque_lotes.id = estoque_movimentacoes.lote_id)))
     JOIN public.estoque_unidades_medidas ON ((estoque_produtos.unidade_medida_id = estoque_unidades_medidas.id)))
     JOIN ( SELECT sum(estoque_tanques.quantidade_disponivel) AS saldo_atual,
            estoque_lotes_1.produto_id,
            estoque_tanques.almoxarifado_id
           FROM (public.estoque_tanques
             JOIN public.estoque_lotes estoque_lotes_1 ON ((estoque_tanques.lote_id = estoque_lotes_1.id)))
          GROUP BY estoque_lotes_1.produto_id, estoque_tanques.almoxarifado_id) saldo_atual_query ON (((estoque_lotes.produto_id = saldo_atual_query.produto_id) AND (estoque_movimentacoes.almoxarifado_id = saldo_atual_query.almoxarifado_id))))
     LEFT JOIN ( SELECT sum(estoque_movimentacoes_1.quantidade) AS saldo_municipe,
            estoque_lotes_1.produto_id,
            estoque_movimentacoes_1.almoxarifado_id,
            estoque_movimentacoes_1.id AS estoque_movimentacoes_id
           FROM (public.estoque_movimentacoes estoque_movimentacoes_1
             JOIN public.estoque_lotes estoque_lotes_1 ON ((estoque_movimentacoes_1.lote_id = estoque_lotes_1.id)))
          WHERE (estoque_movimentacoes_1.tipo_movimentacao_id = 9)
          GROUP BY estoque_lotes_1.produto_id, estoque_movimentacoes_1.id, estoque_movimentacoes_1.almoxarifado_id) saldo_municipe_query ON (((estoque_produtos.id = saldo_municipe_query.produto_id) AND (estoque_movimentacoes.almoxarifado_id = saldo_municipe_query.almoxarifado_id) AND (estoque_movimentacoes.id = saldo_municipe_query.estoque_movimentacoes_id))))
     LEFT JOIN ( SELECT sum(estoque_movimentacoes_1.quantidade) AS saldo_requisicao_consumo,
            estoque_lotes_1.produto_id,
            estoque_movimentacoes_1.almoxarifado_id,
            estoque_movimentacoes_1.id AS estoque_movimentacoes_id
           FROM (public.estoque_movimentacoes estoque_movimentacoes_1
             JOIN public.estoque_lotes estoque_lotes_1 ON ((estoque_movimentacoes_1.lote_id = estoque_lotes_1.id)))
          WHERE (estoque_movimentacoes_1.tipo_movimentacao_id = 5)
          GROUP BY estoque_lotes_1.produto_id, estoque_movimentacoes_1.id, estoque_movimentacoes_1.almoxarifado_id) saldo_requisicao_consumo_query ON (((estoque_produtos.id = saldo_requisicao_consumo_query.produto_id) AND (estoque_movimentacoes.almoxarifado_id = saldo_requisicao_consumo_query.almoxarifado_id) AND (estoque_movimentacoes.id = saldo_requisicao_consumo_query.estoque_movimentacoes_id))))
     LEFT JOIN ( SELECT DISTINCT ON (estoque_pedidos_reposicoes_automaticas_produtos.produto_id, estoque_pedidos_reposicoes_automaticas.almoxarifado_destino_id) estoque_pedidos_reposicoes_automaticas_produtos.produto_id,
            estoque_pedidos_reposicoes_automaticas.almoxarifado_destino_id AS almoxarifado_id,
            estoque_pedidos_reposicoes_automaticas_produtos.triagem
           FROM (public.estoque_pedidos_reposicoes_automaticas_produtos
             JOIN public.estoque_pedidos_reposicoes_automaticas ON ((estoque_pedidos_reposicoes_automaticas.id = estoque_pedidos_reposicoes_automaticas_produtos.pedido_reposicao_automatica_id)))
          ORDER BY estoque_pedidos_reposicoes_automaticas_produtos.produto_id, estoque_pedidos_reposicoes_automaticas.almoxarifado_destino_id, estoque_pedidos_reposicoes_automaticas_produtos.created_at DESC) ultimos_pedidos_reposicoes ON (((estoque_movimentacoes.almoxarifado_id = ultimos_pedidos_reposicoes.almoxarifado_id) AND (estoque_lotes.produto_id = ultimos_pedidos_reposicoes.produto_id))))
  WHERE (estoque_movimentacoes.tipo_movimentacao_id = ANY (ARRAY[9, 5]));


