CREATE VIEW public.vw_regulacao_solicitacoes_posicoes AS
 SELECT row_number() OVER (ORDER BY regulacao_solicitacoes.tipo_prioridade_id, COALESCE(regulacao_solicitacoes.data_solicitacao, (regulacao_solicitacoes.created_at)::date), (array_agg(regulacao_solicitacoes.id))) AS id,
    COALESCE(regulacao_solicitacoes.data_solicitacao, (regulacao_solicitacoes.created_at)::date) AS data_solicitacao,
    regulacao_solicitacoes.status,
    regulacao_solicitacoes.tipo_atendimento_agenda_id,
    regulacao_solicitacoes.tipo_prioridade_id,
    array_agg(regulacao_solicitacoes.id) AS ids
   FROM public.regulacao_solicitacoes
  WHERE ((regulacao_solicitacoes.status)::text <> ALL (ARRAY[('cancelado'::character varying)::text, ('agendado'::character varying)::text]))
  GROUP BY COALESCE(regulacao_solicitacoes.data_solicitacao, (regulacao_solicitacoes.created_at)::date), regulacao_solicitacoes.tipo_atendimento_agenda_id, regulacao_solicitacoes.tipo_prioridade_id, regulacao_solicitacoes.status, regulacao_solicitacoes.municipe_id, regulacao_solicitacoes.ocupacao_id, regulacao_solicitacoes.procedimento_id;


