    row_number() OVER (ORDER BY COALESCE(agendamentos.data, sessoes_terapia_ocupacional.data, sadts.data, atendimento_odontologicos.data), COALESCE(agendamentos.hora, sessoes_terapia_ocupacional.hora, sadts.hora, atendimento_odontologicos.hora), vw_regulacao_solicitacoes_posicoes.id, regulacao_solicitacoes.created_at) AS id
   FROM (((((public.regulacao_solicitacoes
     JOIN public.vw_regulacao_solicitacoes_posicoes ON ((regulacao_solicitacoes.id = ANY (vw_regulacao_solicitacoes_posicoes.ids))))
     LEFT JOIN public.agendamentos ON (((agendamentos.id = regulacao_solicitacoes.agendavel_id) AND ((regulacao_solicitacoes.agendavel_type)::text = 'Agendamento'::text))))
