     LEFT JOIN public.atendimento_odontologicos ON (((atendimento_odontologicos.id = regulacao_solicitacoes.agendavel_id) AND ((regulacao_solicitacoes.agendavel_type)::text = 'AtendimentoOdontologico'::text))))
