--
-- Name: rp_gestantes_puerperas; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.rp_gestantes_puerperas AS
 SELECT DISTINCT unidades_saude.id AS unidade_saude_id,
    unidades_saude.nome AS unidade_saude,
    equipes.area,
    municipes.micro_area,
    municipes.id AS municipe_id,
    municipes.nome,
    public.idade(municipes.data_nascimento) AS idade,
    municipes.codigo_cns AS cns,
    vw_numero_prontuarios_familiares.numero_prontuario AS prontuario_familiar,
    to_char((esus_atendimentos_individuais.dum)::timestamp with time zone, 'DD/MM/YY'::text) AS dum,
    public.calcula_dpp((esus_atendimentos_individuais.dum)::timestamp without time zone) AS dpp,
    public.calcula_idade_gestacional((esus_atendimentos_individuais.dum)::timestamp without time zone) AS ig,
    to_char((ultimo_pre_natal.data)::timestamp with time zone, 'DD/MM/YY'::text) AS ultimo_atendimento,
    public.boolean_ptbr(esus_atendimentos_individuais.vacinacao_em_dia) AS vacinacao_em_dia,
    vdrl_sifilis.vdrl_solicitado,
    vdrl_sifilis.vdrl_avaliado,
    to_char((atendimento_puerpera.data)::timestamp with time zone, 'DD/MM/YY'::text) AS ultimo_puerpera,
    to_char((atendimento_odontologico.data)::timestamp with time zone, 'DD/MM/YY'::text) AS data_odontologico,
    to_char((visita_domiciliar.data)::timestamp with time zone, 'DD/MM/YY'::text) AS data_visita
   FROM ((((((((((((public.municipes
     JOIN public.esus_atendimentos_individuais ON ((municipes.id = esus_atendimentos_individuais.municipe_id)))
     LEFT JOIN public.profissionais ON ((profissionais.id = esus_atendimentos_individuais.profissional_id)))
     LEFT JOIN public.equipes_profissionais ON ((equipes_profissionais.profissional_id = profissionais.id)))
     LEFT JOIN public.equipes ON ((equipes.id = equipes_profissionais.equipe_id)))
     LEFT JOIN public.unidades_saude ON ((unidades_saude.id = esus_atendimentos_individuais.unidade_saude_id)))
     LEFT JOIN public.vw_numero_prontuarios_familiares ON ((vw_numero_prontuarios_familiares.municipe_id = municipes.id)))
     JOIN ( SELECT esus_atendimentos_individuais_1.municipe_id,
            max(esus_atendimentos_individuais_1.id) AS ultimo_atendimento_indidvidual_id,
            min(esus_atendimentos_individuais_1.data) AS data_primeiro_atendimento
           FROM ((public.esus_atendimentos_individuais esus_atendimentos_individuais_1
             LEFT JOIN public.esus_atendimentos_individuais_ciaps ON ((esus_atendimentos_individuais_ciaps.atendimento_individual_id = esus_atendimentos_individuais_1.id)))
             LEFT JOIN public.ciaps ON ((ciaps.id = esus_atendimentos_individuais_ciaps.ciap_id)))
          WHERE (((ciaps.codigo)::text = 'ABP001'::text) OR ((ciaps.codigo)::text = 'ABP002'::text))
          GROUP BY esus_atendimentos_individuais_1.municipe_id) condicoes ON ((condicoes.municipe_id = municipes.id)))
     LEFT JOIN ( SELECT max(esus_atendimentos_individuais_1.id) AS atendimento_individual_id,
            esus_atendimentos_individuais_1.municipe_id,
            public.boolean_ptbr(esus_atendimentos_individuais_exames.solicitado) AS vdrl_solicitado,
            public.boolean_ptbr(esus_atendimentos_individuais_exames.avaliado) AS vdrl_avaliado
           FROM ((public.esus_atendimentos_individuais esus_atendimentos_individuais_1
             LEFT JOIN public.esus_atendimentos_individuais_exames ON ((esus_atendimentos_individuais_exames.atendimento_individual_id = esus_atendimentos_individuais_1.id)))
             LEFT JOIN public.esus_auxiliar_exames ON (((esus_atendimentos_individuais_exames.examinavel_id = esus_auxiliar_exames.id) AND ((esus_atendimentos_individuais_exames.examinavel_type)::text = 'Esus::Auxiliar::Exame'::text))))
          WHERE ((esus_auxiliar_exames.ab_codigo)::text = 'ABEX019'::text)
          GROUP BY esus_atendimentos_individuais_exames.solicitado, esus_atendimentos_individuais_exames.avaliado, esus_auxiliar_exames.ab_codigo, esus_atendimentos_individuais_1.municipe_id) vdrl_sifilis ON ((vdrl_sifilis.atendimento_individual_id = condicoes.ultimo_atendimento_indidvidual_id)))
     LEFT JOIN ( SELECT esus_atendimentos_individuais_1.id AS atendimento_individual_id,
            esus_atendimentos_individuais_1.municipe_id,
