--
-- Name: vw_agendamentos_profissionais_responsaveis; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_agendamentos_profissionais_responsaveis AS
 WITH usuarios_profissionais AS (
         SELECT profissionais.id,
            profissionais.nome,
            usuarios.id AS usuario_id
           FROM (public.profissionais
             LEFT JOIN public.usuarios ON ((profissionais.id = usuarios.profissional_id)))
        )
 SELECT agendamentos.id,
    atendimentos_ambulatoriais.ficha,
    unidades_saude.nome AS unidade_saude,
    profissionais_recepcoes.nome AS recepcao,
    profissionais_consultas.nome AS consulta,
    profissionais_acolhimentos.nome AS acolhimento,
    profissionais_bpa.nome AS faturamento_bpa,
    profissionais_bpa_i.nome AS faturamento_bpa_i,
    profissionais_bpa_c.nome AS faturamento_bpa_c
   FROM ((((((((((((((public.agendamentos
     JOIN public.agendas ON ((agendas.id = agendamentos.agenda_id)))
     JOIN public.unidades_saude_ocupacoes ON ((agendas.unidade_saude_ocupacao_id = unidades_saude_ocupacoes.id)))
     JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
     JOIN public.profissionais profissionais_consultas ON ((profissionais_consultas.id = agendas.profissional_id)))
     LEFT JOIN public.atendimentos_ambulatoriais ON ((atendimentos_ambulatoriais.agendamento_id = agendamentos.id)))
     LEFT JOIN usuarios_profissionais profissionais_recepcoes ON ((profissionais_recepcoes.usuario_id = atendimentos_ambulatoriais.usuario_id)))
     LEFT JOIN public.acolhimentos ON (((acolhimentos.acolhivel_id = atendimentos_ambulatoriais.id) AND ((acolhimentos.acolhivel_type)::text = 'AtendimentoAmbulatorial'::text))))
     LEFT JOIN usuarios_profissionais profissionais_acolhimentos ON ((profissionais_acolhimentos.id = acolhimentos.profissional_id)))
     LEFT JOIN public.faturamento_lancamentos_bpa ON (((faturamento_lancamentos_bpa.lancavel_id = agendamentos.id) AND ((faturamento_lancamentos_bpa.lancavel_type)::text = 'Agendamento'::text))))
     LEFT JOIN usuarios_profissionais profissionais_bpa ON ((profissionais_bpa.usuario_id = faturamento_lancamentos_bpa.usuario_id)))
     LEFT JOIN public.faturamento_lancamentos_bpa_i ON (((faturamento_lancamentos_bpa_i.lancavel_id = agendamentos.id) AND ((faturamento_lancamentos_bpa_i.lancavel_type)::text = 'Agendamento'::text))))
     LEFT JOIN usuarios_profissionais profissionais_bpa_i ON ((profissionais_bpa_i.usuario_id = faturamento_lancamentos_bpa_i.usuario_id)))
     LEFT JOIN public.faturamento_lancamentos_bpa_c ON (((faturamento_lancamentos_bpa_c.lancavel_id = agendamentos.id) AND ((faturamento_lancamentos_bpa_c.lancavel_type)::text = 'Agendamento'::text))))
     LEFT JOIN usuarios_profissionais profissionais_bpa_c ON ((profissionais_bpa_c.usuario_id = faturamento_lancamentos_bpa_c.usuario_id)));


--
-- Name: vw_agendas_agendamentos_dias; Type: VIEW; Schema: public; Owner: -
