CREATE VIEW public.vw_agendas_agendamentos_dias AS
 SELECT agendamentos.agenda_id,
    agendamentos.data,
    count(agendamentos.id) AS consumo
   FROM public.agendamentos
  WHERE ((agendamentos.canceled_at IS NULL) AND (NOT agendamentos.demanda_espontanea) AND (agendamentos.data >= ('now'::text)::date))
  GROUP BY agendamentos.agenda_id, agendamentos.data;


