CREATE VIEW public.vw_agendas_datas_permitidas_dinamico AS
 SELECT datas.data,
    datas.agenda_id,
    public.agenda_dia_semana(datas.data) AS dia_atendimento_id
   FROM ( SELECT (generate_series((('now'::text)::date)::timestamp without time zone, (public.agenda_ultima_data(agendas.*))::timestamp without time zone, '1 day'::interval))::date AS data,
            agendas.id AS agenda_id
           FROM public.agendas) datas;


--
-- Name: vw_agendas_dias_incompletos; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_agendas_dias_incompletos AS
 SELECT horarios.data,
    horarios.agenda_id,
    horarios.vagas,
    (COALESCE(agendamentos.consumo, (0)::numeric))::bigint AS consumo
   FROM (( SELECT agendas_horarios_ativos.data,
            agendas_horarios_ativos.agenda_id,
            sum(agendas_horarios_ativos.vagas) AS vagas
           FROM public.agendas_horarios_ativos
          GROUP BY agendas_horarios_ativos.data, agendas_horarios_ativos.agenda_id) horarios
     LEFT JOIN ( SELECT vw_agendas_agendamentos_dias.data,
            vw_agendas_agendamentos_dias.agenda_id,
            sum(vw_agendas_agendamentos_dias.consumo) AS consumo
           FROM public.vw_agendas_agendamentos_dias
          GROUP BY vw_agendas_agendamentos_dias.data, vw_agendas_agendamentos_dias.agenda_id) agendamentos ON (((horarios.data = agendamentos.data) AND (horarios.agenda_id = agendamentos.agenda_id))));


--
-- Name: vw_feriados_futuros; Type: MATERIALIZED VIEW; Schema: public; Owner: -
--

CREATE MATERIALIZED VIEW public.vw_feriados_futuros AS
 SELECT (format('%s-%s-%s'::text,
        CASE feriados_pontos_facultativos.fixo
            WHEN true THEN ano_extraido.ano_extraido
            ELSE (feriados_pontos_facultativos.ano)::integer
        END, feriados_pontos_facultativos.mes, feriados_pontos_facultativos.dia))::date AS data
   FROM public.feriados_pontos_facultativos,
    generate_series((date_part('year'::text, ('now'::text)::date))::integer, (date_part('year'::text, public.agenda_ultima_data_disponivel()))::integer) ano_extraido(ano_extraido)
  WHERE (feriados_pontos_facultativos.deleted_at IS NULL)
  GROUP BY (format('%s-%s-%s'::text,
        CASE feriados_pontos_facultativos.fixo
            WHEN true THEN ano_extraido.ano_extraido
            ELSE (feriados_pontos_facultativos.ano)::integer
        END, feriados_pontos_facultativos.mes, feriados_pontos_facultativos.dia))::date
  WITH NO DATA;


