CREATE VIEW public.vw_agendas_dias_indisponiveis AS
 SELECT datas_incompletas.agenda_id,
    datas_incompletas.data_inicio,
    datas_incompletas.data_fim,
    datas_incompletas.manutencao
   FROM ( SELECT agendas_bloqueios.agenda_id,
            agendas_bloqueios.data_inicio,
            agendas_bloqueios.data_fim,
            false AS manutencao
           FROM public.agendas_bloqueios
          WHERE (agendas_bloqueios.escolher_horario_bloqueio = false)
        UNION ALL
         SELECT agendas.id AS agenda_id,
            manutencoes_equipamentos.data_inicio,
            manutencoes_equipamentos.data_fim,
            true AS manutencao
           FROM public.agendas,
            public.manutencoes_equipamentos
          WHERE (agendas.equipamento_utilizavel_id = manutencoes_equipamentos.equipamento_utilizavel_id)
        UNION ALL
         SELECT NULL::bigint AS agenda_id,
            vw_feriados_futuros.data AS data_inicio,
            vw_feriados_futuros.data AS data_fim,
            false AS manutencao
           FROM public.vw_feriados_futuros) datas_incompletas
  WHERE (datas_incompletas.data_fim >= ('now'::text)::date);


