-- Name: vw_agendas_horarios_ativos_dinamico; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_agendas_horarios_ativos_dinamico AS
 SELECT DISTINCT datas.data,
    horarios.id,
    horarios.agendavel_id,
    horarios.agendavel_type,
    horarios.dia_atendimento_id,
    horarios.hora,
    horarios.tipo_atividade_agendamento_id,
    horarios.vagas,
    horarios.ocupacao_id,
    horarios.procedimento_id,
    horarios.unidade_saude_id,
    horarios.profissional_id,
    horarios.agenda_id,
    horarios.especifica,
    horarios.distribuido,
    COALESCE(agendas_especificas_horarios.observacao, agendas_mapas_horarios.observacao) AS observacao,
    horarios.agenda_especifica_complementar
   FROM (((public.agendas_horarios_disponiveis horarios
     LEFT JOIN public.agendas_especificas_horarios ON (((agendas_especificas_horarios.id = horarios.agendavel_id) AND ((horarios.agendavel_type)::text = 'AgendaEspecificaHorario'::text))))
     LEFT JOIN public.agendas_mapas_horarios ON (((agendas_mapas_horarios.id = horarios.agendavel_id) AND ((horarios.agendavel_type)::text = 'AgendaMapaHorario'::text))))
     JOIN public.vw_agendas_datas_permitidas_dinamico datas ON ((horarios.agenda_id = datas.agenda_id)))
  WHERE
        CASE
            WHEN (horarios.especifica IS TRUE) THEN ((horarios.data_inicio = datas.data) AND (horarios.inativo IS NOT TRUE))
            ELSE ((datas.dia_atendimento_id = horarios.dia_atendimento_id) AND (datas.data >= horarios.data_inicio) AND (datas.data <= horarios.data_fim) AND (horarios.inativo IS NOT TRUE))
        END;


