           FROM public.agendamentos agendamentos_1
          WHERE ((agendamentos_1.demanda_espontanea IS FALSE) AND (agendamentos_1.canceled_at IS NULL) AND (agendamentos_1.data >= ('now'::text)::date))
        )
 SELECT horarios.data,
    horarios.agenda_horario_disponivel_id AS id,
    horarios.agendavel_id,
    horarios.agendavel_type,
    horarios.dia_atendimento_id,
    horarios.hora,
    horarios.tipo_atividade_agendamento_id,
    horarios.vagas,
    horarios.ocupacao_id,
    horarios.procedimento_id,
    horarios.unidade_saude_id,
    horarios.profissional_id,
    horarios.agenda_id,
    horarios.especifica,
    horarios.distribuido,
    horarios.observacao,
    count(agendamentos.*) AS consumo,
    horarios.agenda_especifica_complementar
   FROM (public.agendas_horarios_ativos horarios
     LEFT JOIN agendamentos_filtrados agendamentos ON (((agendamentos.agenda_id = horarios.agenda_id) AND (agendamentos.data = horarios.data) AND (agendamentos.tipo_atividade_agendamento_id = horarios.tipo_atividade_agendamento_id) AND ((COALESCE(agendamentos.observacao, ''::character varying))::text = (COALESCE(horarios.observacao, ''::character varying))::text) AND (agendamentos.especifica = horarios.especifica) AND ((horarios.dia_atendimento_id IS NULL) OR (horarios.dia_atendimento_id = agendamentos.dia_atendimento_id)) AND
        CASE
            WHEN (agendamentos.distribuido IS TRUE) THEN
            CASE
                WHEN (horarios.distribuido IS TRUE) THEN (agendamentos.hora = horarios.hora)
                ELSE ((agendamentos.hora >= horarios.hora) AND (agendamentos.hora <= (horarios.hora + '00:59:00'::interval)))
            END
            ELSE
            CASE
                WHEN (horarios.distribuido IS TRUE) THEN (EXISTS ( SELECT 1
                   FROM public.agendas_horarios_ativos horarios_sub
                  WHERE ((horarios_sub.agendavel_id = horarios.agendavel_id) AND ((horarios_sub.agendavel_type)::text = (horarios.agendavel_type)::text) AND (horarios_sub.tipo_atividade_agendamento_id = horarios.tipo_atividade_agendamento_id) AND ((horarios_sub.hora >= agendamentos.hora) AND (horarios_sub.hora <= (agendamentos.hora + '00:59:00'::interval))) AND (NOT (EXISTS ( SELECT 1
                           FROM agendamentos_filtrados agendamentos_sub_distr
                          WHERE ((horarios_sub.hora = agendamentos_sub_distr.hora) AND (agendamentos.especifica = agendamentos_sub_distr.especifica) AND (agendamentos.agenda_id = agendamentos_sub_distr.agenda_id) AND ((agendamentos_sub_distr.observacao)::text = (agendamentos.observacao)::text) AND (agendamentos.tipo_atividade_agendamento_id = agendamentos_sub_distr.tipo_atividade_agendamento_id) AND (agendamentos_sub_distr.distribuido IS TRUE))))))
                  ORDER BY horarios_sub.hora DESC
                 LIMIT ( SELECT count(*) AS count
                           FROM agendamentos_filtrados agendamentos_sub
                          WHERE ((agendamentos.hora = agendamentos_sub.hora) AND (agendamentos.especifica = agendamentos_sub.especifica) AND (agendamentos.agenda_id = agendamentos_sub.agenda_id) AND (agendamentos_sub.data = agendamentos.data) AND (agendamentos.tipo_atividade_agendamento_id = agendamentos_sub.tipo_atividade_agendamento_id) AND ((agendamentos_sub.observacao)::text = (agendamentos.observacao)::text) AND (agendamentos_sub.distribuido IS FALSE)))))
                ELSE (agendamentos.hora = horarios.hora)
            END
        END)))
  WHERE (horarios.data >= ('now'::text)::date)
  GROUP BY horarios.agenda_horario_disponivel_id, horarios.data, horarios.agendavel_id, horarios.agendavel_type, horarios.dia_atendimento_id, horarios.hora, horarios.tipo_atividade_agendamento_id, horarios.vagas, horarios.ocupacao_id, horarios.procedimento_id, horarios.unidade_saude_id, horarios.profissional_id, horarios.agenda_id, horarios.especifica, horarios.distribuido, horarios.observacao, horarios.agenda_especifica_complementar;


