            public.municipe_prontuario_unidade(municipes.*, unidades_saude.*) AS prontuario,
            agendas.grupo_atendimento_id,
                CASE
                    WHEN (agendamentos.canceled_at IS NULL) THEN 'NÃO'::text
                    ELSE 'SIM'::text
                END AS cancelado,
            public.pega_situacao_presenca_agenda(agendamentos.id) AS situacao,
            concat(municipes.telefone_residencial, ' ', municipes.telefone_celular, ' ', municipes.telefone_comercial) AS telefones,
            agendamentos.especifica
           FROM ((((((public.agendamentos
             JOIN public.municipes ON ((municipes.id = agendamentos.municipe_id)))
             LEFT JOIN public.atendimentos_ambulatoriais ON ((atendimentos_ambulatoriais.agendamento_id = agendamentos.id)))
             LEFT JOIN public.situacoes_presencas_atendimentos ON ((situacoes_presencas_atendimentos.id = atendimentos_ambulatoriais.situacao_presenca_atendimento_id)))
             JOIN public.agendas ON ((agendas.id = agendamentos.agenda_id)))
             JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
             JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
          ORDER BY agendamentos.data)
        UNION ALL
        ( SELECT unidades_saude.id AS unidade_saude_id,
            agendas.profissional_id,
            NULL::bigint AS agendamento_id,
            municipes.id AS municipe_id,
            unidades_saude_ocupacoes.ocupacao_id,
