                CASE
                    WHEN ((sessoes_terapia_ocupacional.situacao)::text = 'cancelado'::text) THEN 'SIM'::text
                    ELSE 'NÃO'::text
                END AS cancelado,
            situacoes_presencas_atendimentos.id AS situacao_id,
            concat(municipes.telefone_residencial, ' ', municipes.telefone_celular, ' ', municipes.telefone_comercial) AS telefones,
