           FROM ((((((public.sessoes_terapia_ocupacional
             JOIN public.solicitacoes_terapia_ocupacional ON ((solicitacoes_terapia_ocupacional.id = sessoes_terapia_ocupacional.solicitacao_terapia_ocupacional_id)))
             JOIN public.municipes ON ((municipes.id = solicitacoes_terapia_ocupacional.municipe_id)))
             LEFT JOIN public.situacoes_presencas_atendimentos ON ((situacoes_presencas_atendimentos.id = sessoes_terapia_ocupacional.situacao_presenca_atendimento_id)))
             JOIN public.agendas ON ((agendas.id = sessoes_terapia_ocupacional.agenda_id)))
             JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
             JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
          ORDER BY sessoes_terapia_ocupacional.data)
        UNION ALL
        ( SELECT unidades_saude.id AS unidade_saude_id,
            agendas.profissional_id,
            NULL::bigint AS agendamento_id,
            municipes.id AS municipe_id,
            unidades_saude_ocupacoes.ocupacao_id,
