                CASE
                    WHEN ((sadts.situacao)::text = 'cancelado'::text) THEN 'SIM'::text
                    ELSE 'NÃO'::text
                END AS cancelado,
                CASE
                    WHEN ((sadts.situacao)::text = 'finalizado'::text) THEN 1
                    ELSE NULL::integer
                END AS situacao_id,
            concat(municipes.telefone_residencial, ' ', municipes.telefone_celular, ' ', municipes.telefone_comercial) AS telefones,
            sadts.especifica
           FROM (((((public.sadts
