                END AS cancelado,
                CASE
                    WHEN ((solicitacao_atendimento_odontologicos.situacao)::text = 'finalizado'::text) THEN 1
                    ELSE NULL::integer
                END AS situacao_id,
            concat(municipes.telefone_residencial, ' ', municipes.telefone_celular, '', municipes.telefone_comercial) AS telefones,
            atendimento_odontologicos.especifica
           FROM ((((((((((public.atendimento_odontologicos
             JOIN public.agendas ON ((agendas.id = atendimento_odontologicos.agenda_id)))
             LEFT JOIN public.profissionais ON ((profissionais.id = agendas.profissional_id)))
             LEFT JOIN public.procedimentos ON ((procedimentos.id = agendas.procedimento_id)))
             LEFT JOIN public.grupos_atendimentos ON ((grupos_atendimentos.id = agendas.grupo_atendimento_id)))
             JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
             JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
             LEFT JOIN public.locais ON ((locais.id = unidades_saude.local_id)))
             JOIN public.ocupacoes ON ((ocupacoes.id = unidades_saude_ocupacoes.ocupacao_id)))
             JOIN public.solicitacao_atendimento_odontologicos ON ((solicitacao_atendimento_odontologicos.id = atendimento_odontologicos.solicitacao_atendimento_odontologico_id)))
             JOIN public.municipes ON ((municipes.id = solicitacao_atendimento_odontologicos.municipe_id)))
          ORDER BY atendimento_odontologicos.data)) base_query;


