    condicoes_situacoes_saude_agg.nome AS condicao_situacao_saude,
    count(*) AS agendada,
    sum(
        CASE
            WHEN ((atendimentos_ambulatoriais.situacao_presenca_atendimento_id IS NULL) OR (atendimentos_ambulatoriais.situacao_presenca_atendimento_id <> public.obter_situacao_presenca_atendimento_paciente_presente())) THEN 1
            ELSE 0
        END) AS absenteismo
   FROM ((public.agendamentos
     LEFT JOIN public.atendimentos_ambulatoriais ON ((atendimentos_ambulatoriais.agendamento_id = agendamentos.id)))
     LEFT JOIN ( SELECT agendas_condicoes_situacoes_saude.agenda_id,
            string_agg((condicoes_situacoes_saude.nome)::text, ', '::text ORDER BY (condicoes_situacoes_saude.nome)::text) AS nome
           FROM (public.agendas_condicoes_situacoes_saude
             JOIN public.condicoes_situacoes_saude ON ((condicoes_situacoes_saude.id = agendas_condicoes_situacoes_saude.condicao_situacao_saude_id)))
          GROUP BY agendas_condicoes_situacoes_saude.agenda_id) condicoes_situacoes_saude_agg ON ((condicoes_situacoes_saude_agg.agenda_id = agendamentos.agenda_id)))
  WHERE (agendamentos.canceled_at IS NULL)
  GROUP BY agendamentos.agenda_id, agendamentos.data, (public.agendamento_tipo_atividade(agendamentos.*)), condicoes_situacoes_saude_agg.nome;


