CREATE VIEW public.vw_aplicativo_agendas_ocupacoes_configuradas AS
 SELECT unidades_saude_ocupacoes.ocupacao_id,
    unidades_saude_ocupacoes.unidade_saude_id,
        CASE
            WHEN (agendas.por_idade IS TRUE) THEN COALESCE(agendas.idade_inicial, 0)
            WHEN (datasus_procedimentos.id IS NOT NULL) THEN
            CASE
                WHEN (datasus_procedimentos.vl_idade_minima = 9999) THEN 0
                ELSE datasus_procedimentos.vl_idade_minima
            END
            ELSE 0
        END AS idade_minima,
        CASE
            WHEN (agendas.por_idade IS TRUE) THEN COALESCE(agendas.idade_final, 9999)
            WHEN (datasus_procedimentos.id IS NOT NULL) THEN datasus_procedimentos.vl_idade_maxima
            ELSE 9999
        END AS idade_maxima,
        CASE
            WHEN (agendas.por_sexo IS TRUE) THEN agendas.sexo_id
            WHEN (datasus_procedimentos.id IS NOT NULL) THEN (sexos_procedimentos.id)::bigint
            ELSE (( SELECT sexos.id
               FROM public.sexos
              WHERE ((sexos.codigo)::text = 'I'::text)
             LIMIT 1))::bigint
        END AS sexo_id,
    agendas.profissional_id
   FROM ((((((public.ocupacoes ocupacoes_agendas
     JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.ocupacao_id = ocupacoes_agendas.id)))
     JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
     JOIN public.agendas ON ((agendas.unidade_saude_ocupacao_id = unidades_saude_ocupacoes.id)))
     LEFT JOIN public.procedimentos ON ((agendas.procedimento_id = procedimentos.id)))
     LEFT JOIN public.datasus_procedimentos ON ((((datasus_procedimentos.co_procedimento)::text = (procedimentos.codigo)::text) AND ((datasus_procedimentos.dt_competencia)::text = (public.datasus_competencia_atual())::text))))
     LEFT JOIN public.sexos sexos_procedimentos ON (((sexos_procedimentos.codigo)::text = (datasus_procedimentos.tp_sexo)::text)))
  WHERE ((unidades_saude_ocupacoes.bloqueado = false) AND (EXISTS ( SELECT 1
           FROM public.tipos_atendimentos_agendas
          WHERE (((tipos_atendimentos_agendas.nome)::text = 'ATENDIMENTO AMBULATORIAL'::text) AND (tipos_atendimentos_agendas.id = agendas.tipo_atendimento_agenda_id))
         LIMIT 1)) AND (unidades_saude.inativa = false) AND (agendas.deleted_at IS NULL));


