CREATE VIEW public.vw_cross_solicitacoes_ranqueadas AS
 SELECT cross_solicitacoes.id AS solicitacao_id,
    row_number() OVER (ORDER BY COALESCE(cross_solicitacoes.data, (cross_solicitacoes.created_at)::date), cross_solicitacoes.id) AS posicao,
    COALESCE(cross_solicitacoes.data, (cross_solicitacoes.created_at)::date) AS data,
    cross_solicitacoes.status
   FROM public.cross_solicitacoes
  WHERE ((cross_solicitacoes.status)::text <> ALL (ARRAY[('cancelado'::character varying)::text, ('agendado'::character varying)::text]))
  GROUP BY cross_solicitacoes.id, cross_solicitacoes.data, ((cross_solicitacoes.created_at)::date), cross_solicitacoes.status;


