    NULL::text AS endereco_completo,
    municipes.unidade_saude_referencia_id AS unidade_saude_id,
    (municipes.created_at)::date AS data
   FROM (public.esus_controles
     JOIN public.municipes ON (((municipes.id = esus_controles.ficha_id) AND ((esus_controles.ficha_type)::text = 'Esus::CadastroIndividual'::text))))
  WHERE ((esus_controles.ficha_type)::text = 'Esus::CadastroIndividual'::text)
  ORDER BY municipes.created_at)
UNION ALL
( SELECT esus_controles.id AS controle_id,
    esus_controles.ficha_id,
    esus_controles.ficha_type,
    esus_controles.uuid,
    NULL::bigint AS municipe_id,
        CASE
            WHEN ((profissionais.codigo_cns IS NULL) OR ((profissionais.codigo_cns)::text = ''::text)) THEN (profissionais.nome)::text
            ELSE concat(profissionais.codigo_cns, ' - ', profissionais.nome)
