    procedimentos.nome AS descricao,
    procedimentos.codigo
   FROM public.procedimentos
  WHERE (("substring"((procedimentos.codigo)::text, 1, 2) = '02'::text) OR ((procedimentos.codigo)::text = '0101040091'::text))
UNION ALL
