     FULL JOIN public.procedimentos ON (((procedimentos.codigo)::text = (esus_procedimentos_odontologicos.codigo_sigtap)::text)))
  WHERE ((procedimentos.id IS NULL) OR (((procedimentos.codigo)::text <> '0301040079'::text) AND (("substring"((procedimentos.codigo)::text, 1, 2) = ANY (ARRAY['01'::text, '02'::text, '03'::text, '04'::text])) OR ((procedimentos.codigo)::text = ANY (ARRAY[('0701070064'::character varying)::text, ('0701070072'::character varying)::text, ('0701070080'::character varying)::text, ('0701070099'::character varying)::text, ('0701070102'::character varying)::text, ('0701070110'::character varying)::text, ('0701070129'::character varying)::text, ('0701070137'::character varying)::text, ('0701070056'::character varying)::text, ('0701070145'::character varying)::text])))));


