CREATE VIEW public.vw_filas_rastreamento_senhas AS
 SELECT filas_senhas.id AS fila_senha_id,
    filas_senhas.senha,
    filas_senhas.retirada_em,
    filas_senhas.status,
        CASE
            WHEN ((filas_senhas.status)::text = 'fila_atendimento'::text) THEN (ocupacoes.nome)::text
            ELSE NULL::text
        END AS substatus,
    filas_senhas.enfileiravel_type,
    filas_senhas.enfileiravel_id,
    atendimentos_pas.ficha AS ficha_atendimento,
    atendimentos_pas.municipe_id
   FROM ((public.filas_senhas
     JOIN public.atendimentos_pas ON (((filas_senhas.enfileiravel_id = atendimentos_pas.id) AND ((filas_senhas.enfileiravel_type)::text = 'AtendimentoPa'::text))))
     JOIN public.ocupacoes ON ((atendimentos_pas.ocupacao_id = ocupacoes.id)))
  WHERE ((filas_senhas.finalizado IS FALSE) AND (filas_senhas.status IS NOT NULL) AND (atendimentos_pas.hora_cancelamento IS NULL))
  ORDER BY filas_senhas.created_at DESC;


--
-- Name: vw_gestao_leito_status; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_gestao_leito_status AS
 SELECT gestao_leito_leitos.id AS leito_id,
        CASE
            WHEN gestao_leito_leitos.indisponivel THEN 'INDISPONÍVEL'::text
            WHEN (agendamentos_ativos.leito_id IS NOT NULL) THEN 'OCUPADO'::text
            WHEN (agendamentos_agendados.leito_id IS NOT NULL) THEN 'AGENDADO'::text
            WHEN (limpezas_ativas.leito_id IS NOT NULL) THEN 'EM LIMPEZA'::text
            WHEN (manutencoes_ativas.leito_id IS NOT NULL) THEN 'EM MANUTENÇÃO'::text
            WHEN (gestao_leito_leitos.situacao_codigo = 10) THEN 'AGUARDANDO LIMPEZA'::text
            ELSE 'DISPONÍVEL'::text
        END AS status
   FROM ((((((((((public.gestao_leito_leitos
     JOIN public.gestao_leito_salas_quartos ON ((gestao_leito_salas_quartos.id = gestao_leito_leitos.sala_quarto_id)))
     JOIN public.gestao_leito_alas ON ((gestao_leito_alas.id = gestao_leito_salas_quartos.ala_id)))
     JOIN public.gestao_leito_andares ON ((gestao_leito_andares.id = gestao_leito_alas.andar_id)))
     JOIN public.unidades_saude ON ((unidades_saude.id = gestao_leito_andares.unidade_saude_id)))
     LEFT JOIN public.leitos ON ((leitos.id = gestao_leito_leitos.especialidade_leito_id)))
     LEFT JOIN public.tipos_leitos ON ((tipos_leitos.id = leitos.tipo_leito_id)))
     LEFT JOIN ( SELECT gestao_leito_agendamentos.leito_id
           FROM (((public.gestao_leito_agendamentos
             JOIN public.municipes ON ((municipes.id = gestao_leito_agendamentos.municipe_id)))
             JOIN public.gestao_leito_atendimentos ON ((gestao_leito_atendimentos.agendamento_id = gestao_leito_agendamentos.id)))
             LEFT JOIN public.gestao_leito_altas ON ((gestao_leito_altas.atendimento_id = gestao_leito_atendimentos.id)))
          WHERE ((now() >= gestao_leito_atendimentos.created_at) AND ((gestao_leito_altas.id IS NULL) OR ((((gestao_leito_altas.data || ' '::text) || gestao_leito_altas.hora))::timestamp without time zone > now())))
          GROUP BY gestao_leito_agendamentos.leito_id) agendamentos_ativos ON ((agendamentos_ativos.leito_id = gestao_leito_leitos.id)))
     LEFT JOIN ( SELECT gestao_leito_agendamentos.leito_id
           FROM ((public.gestao_leito_agendamentos
             JOIN public.municipes ON ((municipes.id = gestao_leito_agendamentos.municipe_id)))
             LEFT JOIN public.gestao_leito_atendimentos ON ((gestao_leito_atendimentos.agendamento_id = gestao_leito_agendamentos.id)))
          WHERE ((gestao_leito_atendimentos.id IS NULL) AND (gestao_leito_agendamentos.canceled_at IS NULL))
          GROUP BY gestao_leito_agendamentos.leito_id) agendamentos_agendados ON ((agendamentos_agendados.leito_id = gestao_leito_leitos.id)))
     LEFT JOIN ( SELECT gestao_leito_limpezas.leito_id
           FROM public.gestao_leito_limpezas
          WHERE ((now() >= (((gestao_leito_limpezas.data_inicio || ' '::text) || gestao_leito_limpezas.hora_inicio))::timestamp without time zone) AND (now() <= (((gestao_leito_limpezas.data_fim || ' '::text) || gestao_leito_limpezas.hora_fim))::timestamp without time zone) AND (gestao_leito_limpezas.finalizada_em IS NULL))
          GROUP BY gestao_leito_limpezas.leito_id) limpezas_ativas ON ((limpezas_ativas.leito_id = gestao_leito_leitos.id)))
     LEFT JOIN ( SELECT gestao_leito_manutencoes.leito_id
           FROM public.gestao_leito_manutencoes
          WHERE ((gestao_leito_manutencoes.data_inicio <= ('now'::text)::date) AND (gestao_leito_manutencoes.data_fim >= ('now'::text)::date) AND (gestao_leito_manutencoes.finalizada_em IS NULL))
          GROUP BY gestao_leito_manutencoes.leito_id) manutencoes_ativas ON ((manutencoes_ativas.leito_id = gestao_leito_leitos.id)))
  ORDER BY gestao_leito_leitos.id;


