--

CREATE VIEW public.vw_historicos_atendimentos_municipes AS
 SELECT atendimentos_pas.municipe_id,
    atendimentos_pas.data,
    atendimentos_pas.hora,
    'atendimento_pa'::text AS tipo,
    atendimentos_pas.profissional_id,
    atendimentos_pas.ocupacao_id,
    atendimentos_pas.unidade_saude_id,
    'AtendimentoPa'::text AS atendivel_type,
    atendimentos_pas.id AS atendivel_id,
    atendimentos_pas.created_at,
    atendimentos_pas.situacao_atendimento_id AS situacao_presenca_atendimento_id,
        CASE
            WHEN (atendimentos_pas.hora_cancelamento IS NULL) THEN false
            ELSE true
        END AS cancelado,
    0 AS ambulatorial_situacao_presenca_atendimento_id
   FROM public.atendimentos_pas
UNION ALL
 SELECT solicitacoes_terapia_ocupacional.municipe_id,
    sessoes_terapia_ocupacional.data,
    sessoes_terapia_ocupacional.hora,
    'sessao_terapia_ocupacional'::text AS tipo,
    agendas.profissional_id,
    unidades_saude_ocupacoes.ocupacao_id,
    unidades_saude_ocupacoes.unidade_saude_id,
    'SessaoTerapiaOcupacional'::text AS atendivel_type,
    sessoes_terapia_ocupacional.id AS atendivel_id,
    sessoes_terapia_ocupacional.created_at,
    sessoes_terapia_ocupacional.situacao_atendimento_id AS situacao_presenca_atendimento_id,
        CASE
            WHEN (sessoes_terapia_ocupacional.hora_ausencia IS NULL) THEN false
            ELSE true
        END AS cancelado,
    0 AS ambulatorial_situacao_presenca_atendimento_id
   FROM (((public.sessoes_terapia_ocupacional
     LEFT JOIN public.solicitacoes_terapia_ocupacional ON ((solicitacoes_terapia_ocupacional.id = sessoes_terapia_ocupacional.solicitacao_terapia_ocupacional_id)))
     LEFT JOIN public.agendas ON ((agendas.id = sessoes_terapia_ocupacional.agenda_id)))
     LEFT JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
  WHERE ((sessoes_terapia_ocupacional.situacao)::text = 'finalizado'::text)
UNION ALL
 SELECT agendamentos.municipe_id,
    agendamentos.data,
    agendamentos.hora,
    COALESCE(ambulatorial_atendimentos.tipo, 'atendimento_ambulatorial'::character varying) AS tipo,
    agendas.profissional_id,
    unidades_saude_ocupacoes.ocupacao_id,
    unidades_saude_ocupacoes.unidade_saude_id,
        CASE
            WHEN (ambulatorial_atendimentos.id IS NULL) THEN 'AtendimentoAmbulatorial'::text
            ELSE 'Ambulatorial::Atendimento'::text
        END AS atendivel_type,
    COALESCE((ambulatorial_atendimentos.id)::bigint, atendimentos_ambulatoriais.id) AS atendivel_id,
    COALESCE(ambulatorial_atendimentos.created_at, atendimentos_ambulatoriais.created_at) AS created_at,
    COALESCE(ambulatorial_atendimentos.situacao_presenca_atendimento_id, atendimentos_ambulatoriais.situacao_presenca_atendimento_id) AS situacao_presenca_atendimento_id,
        CASE
            WHEN (ambulatorial_atendimentos.id IS NULL) THEN
            CASE
                WHEN (atendimentos_ambulatoriais.hora_ausencia IS NULL) THEN false
                ELSE true
            END
            ELSE
            CASE
                WHEN (ambulatorial_atendimentos.deleted_at IS NULL) THEN false
                ELSE true
            END
        END AS cancelado,
        CASE
            WHEN (ambulatorial_situacoes_presencas_atendimentos.id IS NOT NULL) THEN ambulatorial_situacoes_presencas_atendimentos.id
            ELSE
            CASE
                WHEN (agendamentos.retroativo = true) THEN 1
                ELSE
                CASE
                    WHEN (agendamentos.canceled_at IS NOT NULL) THEN 5
                    ELSE
                    CASE
                        WHEN ((('now'::text)::date > agendamentos.data) OR ((agendamentos.data = ('now'::text)::date) AND (('now'::text)::time with time zone >= ((agendamentos.hora + '01:00:00'::interval))::time with time zone))) THEN 2
                        ELSE
                        CASE
                            WHEN (agendamentos.confirmado = true) THEN 4
                            ELSE 6
                        END
                    END
                END
            END
        END AS ambulatorial_situacao_presenca_atendimento_id
   FROM (((((public.agendamentos
     JOIN public.atendimentos_ambulatoriais ON ((atendimentos_ambulatoriais.agendamento_id = agendamentos.id)))
     LEFT JOIN public.ambulatorial_atendimentos ON ((ambulatorial_atendimentos.agendamento_id = agendamentos.id)))
     LEFT JOIN public.agendas ON ((agendas.id = agendamentos.agenda_id)))
     LEFT JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
     LEFT JOIN public.ambulatorial_situacoes_presencas_atendimentos ON ((atendimentos_ambulatoriais.situacao_presenca_atendimento_id = ambulatorial_situacoes_presencas_atendimentos.id)))
  WHERE (agendamentos.canceled_at IS NULL)
UNION ALL
 SELECT agendamentos.municipe_id,
    agendamentos.data,
    agendamentos.hora,
    'atendimento_ambulatorial'::character varying AS tipo,
    agendas.profissional_id,
    unidades_saude_ocupacoes.ocupacao_id,
    unidades_saude_ocupacoes.unidade_saude_id,
    'Agendamento'::text AS atendivel_type,
    agendamentos.id AS atendivel_id,
    agendamentos.created_at,
