    sum(query.invalidos) AS invalidos,
    sum(query.concluido) AS concluidos
   FROM ( SELECT horus_posicoes_estoque.id,
            count(horus_posicoes_estoques_itens.id) AS invalidos,
            0 AS concluido
           FROM (public.horus_posicoes_estoque
