    municipes_consolidaveis.fonetica_nome,
    municipes_consolidaveis.fonetica_nome_mae,
    municipes_consolidaveis.data_nascimento
   FROM ( SELECT municipes2.codigo_cns,
            municipes2.fonetica_nome,
