          WHERE (municipes.deleted_at IS NULL)
          GROUP BY municipes2.codigo_cns, municipes2.fonetica_nome, municipes2.fonetica_nome_mae, municipes2.data_nascimento
         HAVING (count(*) > 1)
        UNION
         SELECT municipes3.cartao_provisorio AS codigo_cns,
            municipes3.fonetica_nome,
            municipes3.fonetica_nome_mae,
            municipes3.data_nascimento
