          WHERE ((datasus_servicos_classificacoes.dt_competencia)::text = (public.datasus_competencia_atual())::text)) sq_servicos_classificacoes ON ((((sq_servicos_classificacoes.co_servico)::text = (servicos.codigo)::text) AND ((sq_servicos_classificacoes.co_classificacao)::text = (servicos_unidades_saude.codigo_classificacao)::text))));


