-- Name: vw_transferencia_agendas_compatibilidades; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.vw_transferencia_agendas_compatibilidades AS
 WITH agendas_ativas AS (
         SELECT agendas.id,
            agendas.profissional_id,
            agendas.procedimento_id,
            agendas.unidade_saude_ocupacao_id,
            agendas.sexo_id,
            agendas.idade_inicial,
            agendas.idade_final,
            agendas.tipo_atendimento_agenda_id
           FROM (((public.agendas
             JOIN public.unidades_saude_ocupacoes ON ((unidades_saude_ocupacoes.id = agendas.unidade_saude_ocupacao_id)))
             JOIN public.unidades_saude ON ((unidades_saude.id = unidades_saude_ocupacoes.unidade_saude_id)))
             JOIN public.agendas_mapas_periodos ON ((agendas_mapas_periodos.agenda_id = agendas.id)))
          WHERE ((unidades_saude_ocupacoes.bloqueado IS NOT TRUE) AND (unidades_saude.inativa IS NOT TRUE) AND (agendas_mapas_periodos.inativo IS NOT TRUE) AND (agendas.deleted_at IS NULL))
          GROUP BY agendas.id, agendas.profissional_id, agendas.procedimento_id, agendas.unidade_saude_ocupacao_id, agendas.sexo_id, agendas.idade_inicial, agendas.idade_final, agendas.tipo_atendimento_agenda_id
        )
 SELECT agendas_origem.id AS agenda_origem_id,
    agendas_destino.id AS agenda_destino_id,
    agendas_origem.profissional_id AS profissional_origem_id,
    agendas_destino.profissional_id AS profissional_destino_id
   FROM public.agendas agendas_origem,
    agendas_ativas agendas_destino
  WHERE ((agendas_destino.id IS DISTINCT FROM agendas_origem.id) AND (agendas_destino.profissional_id IS DISTINCT FROM agendas_origem.profissional_id) AND (NOT (agendas_destino.procedimento_id IS DISTINCT FROM agendas_origem.procedimento_id)) AND (NOT (agendas_destino.unidade_saude_ocupacao_id IS DISTINCT FROM agendas_origem.unidade_saude_ocupacao_id)) AND (NOT (agendas_destino.sexo_id IS DISTINCT FROM agendas_origem.sexo_id)) AND (NOT (agendas_destino.idade_inicial IS DISTINCT FROM agendas_origem.idade_inicial)) AND (NOT (agendas_destino.idade_final IS DISTINCT FROM agendas_origem.idade_final)) AND (NOT (agendas_destino.tipo_atendimento_agenda_id IS DISTINCT FROM agendas_origem.tipo_atendimento_agenda_id)));


